import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import { useEffect, useState } from 'react';

/* 컴포넌트 임포트 */
import Button from '../ui/Button';

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    padding: 40px;
`;

const TagButton = styled.button`
    margin-right: 10px;
    background-color: #e0e0e0;
    border: none;
    cursor: pointer;
`;


const BlogListPage = () => {

    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [posts, setPosts] = useState([]); // 서버로부터 받은 포스트들을 저장할 상태

    const navigate = useNavigate(); // 페이지 이동

    useEffect(() => {
        fetch('http://localhost:8000/posts/')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }
                return response.json();
            })
            .then(data => {
                setPosts(data);
                setLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setLoading(false);
            });
        }, []);

    // 글 제목 클릭 시 해당 글의 상세 페이지로 이동하는 함수
    const handlePostClick = (postId) => {
        navigate(`/posts/${postId.toString()}`); // postId를 문자열로 변환
      };

    // 태그 버튼 클릭 시 해당 태그를 가진 글로 필터링하는 로직
    const filterPostsByTag = (tag) => {
        navigate(`/tags/${encodeURIComponent(tag)}`);
      };
    
    // 상단 태그 버튼 클릭 시 이벤트
    const handleTagClick = (tag, event) => {
        event.stopPropagation();
        filterPostsByTag(tag);
    };


    return (
    <Wrapper>
        <h1>블로그 메인/리스트 페이지</h1>
        <div>
            {posts.map((post, index) => (
                <div key={index}>
                    <h2 onClick={() => handlePostClick(post.id)} style={{ cursor: 'pointer' }}>
                        {post.title}
                    </h2>
                    {post.tags.map((tag, tagIndex) => (
                        <span key={tagIndex} style={{marginRight: "10px"}} onClick={(event) => handleTagClick(tag, event)}>
                            #{tag}
                        </span>
                    ))}
                </div>
            ))}
        </div>
    </Wrapper>
    );
};

export default BlogListPage;
