import React from 'react';
import { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

import HeaderComponent from '../ui/HeaderComponent';

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
`;

const ImgTagBox = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    gap: 40px;
    margin-bottom: 30px;
`;

const BackgroundImg = styled.div`
    width: 100%;
    height: 300px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-image: url('${process.env.PUBLIC_URL}/background.JPG');
    background-size: cover;
    background-position: center;
    opacity: 0.5;
    color: white;
    font-family: 'Pretendard-ExtraBold';
    font-size: 60px;
    text-shadow: 0px 0px 3px rgba(0, 0, 0, 0.1);
`;


const PostCategoryContainer = styled.div`
    display: flex;
    padding-top: 30px;
    padding-left: 90px;
    padding-right: 90px;
    flex-direction: row;
    align-items: flex-start;
    gap: 50px;
    justify-content: center; /* Ï∂îÍ∞Ä: ÏûêÏãù ÏöîÏÜåÎì§Ïù¥ Ï§ëÏïôÏóê ÏúÑÏπòÌïòÎèÑÎ°ù Ìï® */
`;

const TagContainer = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
    width: 600px;
    gap: 15px 7px;
`;

const CategoryContainer = styled.div`
    display: flex;
    min-width: 300px; /* ÏµúÏÜå ÎÑàÎπÑ ÏÑ§Ï†ï */
    max-width: 300px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
    flex-direction: column;
    gap: 9px;
`;

const Tags = styled.span`
    padding-left: 12px;
    padding-right: 12px;
    display: flex;
    height: 30px;
    cursor: pointer;
    background-color: #EBEBEB;
    color: ${props => props.isSelected ? '#3b82f6' : '#666666'}; /* Ï°∞Í±¥Î∂Ä ÏÉâÏÉÅ Ï†ÅÏö© */
    text-align: center;
    font-family: 'Pretendard-SemiBold';
    font-size: 13px;
    justify-content: center;
    align-items: center; /* Ïù¥ Ï§ÑÏùÑ Ï∂îÍ∞ÄÌïòÏÑ∏Ïöî */
    border-radius: 30px;
    transition: color 0.1s ease;
    &:hover {
        color: #3b82f6;
    }
`;

const SearchForm = styled.form`
    display: flex;
    flex-direction: row;
    margin-bottom: 20px;
`;

const SearchInput = styled.input`
    height: 30px;
    padding-left: 10px;
    border-radius: 15px;
    border: none;
    box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
    font-family: 'Pretendard-Medium';
`;

const SearchButton = styled.button`
  padding: 5px 10px;
  border-radius: 15px;
  border: none;
  background-color: #EBEBEB;
  color: #666666;
  cursor: pointer;
  margin-left: 10px;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
`;

const PostWrapper = styled.div`
    display: flex;
    flex-direction: column;
    min-width: 750px; /* ÏµúÏÜå ÎÑàÎπÑ ÏÑ§Ï†ï */
    max-width: 750px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
    gap: 16px;
