import React from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import { useEffect, useState } from 'react';

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    padding: 40px;
`;

const TagContainer = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
    //width: 600px;
    gap: 7px;
`;

const Tags = styled.span`
    padding-top: 3px;
    padding-bottom: 3px;
    padding-right: 8px;
    padding-left: 8px;
    background-color: #EBEBEB;
    text-align: center;
    font-size: 13px;
    justify-content: center;
    color: #666666;
    border-radius: 5px;
`;

const BlogListPage = () => {

    // 로딩 및 에러 표시 
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // 서버로부터 받은 포스트들을 저장할 상태
    const [posts, setPosts] = useState([]); 

    // 태그 및 카테고리 정의
    const [tags, setTags] = useState([]);
    const [categories, setCategories] = useState(['ALL', 'Data Analytics TEAM', 'Data Engineering TEAM', 'Data Science TEAM', ]);
    const [selectedTags, setSelectedTags] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');

    const [searchParams, setSearchParams] = useSearchParams();

    // 페이지 이동
    const navigate = useNavigate(); 

    useEffect(() => {
        // 초기 포스트 목록 및 태그, 카테고리 목록 로딩
        const fetchInitialData = async () => {
          try {
            const postsResponse = await fetch('http://localhost:8000/posts/');
            const tagsResponse = await fetch('http://localhost:8000/tags');
            const categoriesResponse = await fetch('http://localhost:8000/categories');
            
            if (!postsResponse.ok || !tagsResponse.ok || !categoriesResponse.ok) {
              throw new Error('Failed to fetch data');
            }
            
            const postsData = await postsResponse.json();
            const tagsData = await tagsResponse.json();
            const categoriesData = await categoriesResponse.json();
            
            setPosts(postsData);
            setTags(tagsData);
            setCategories(['ALL', ...categoriesData]); // Assuming categories data is an array of strings
          } catch (error) {
            setError(error.message);
          } finally {
            setLoading(false);
          }
        };
        fetchInitialData();
      }, []); // 이 useEffect는 컴포넌트가 마운트될 때 한 번만 실행됩니다.

    useEffect(() => {
        // 사용자의 태그, 카테고리 선택에 따른 포스트 목록 업데이트
        const fetchFilteredPosts = async () => {
            try {
              const response = await fetch(`http://localhost:8000/posts?${buildQueryString()}`);
              if (!response.ok) throw new Error('Network response was not ok');
              const data = await response.json();
              setPosts(data);
            } catch (error) {
              setError(error.message);
            } finally {
              setLoading(false);
            }
          };
        
          if (searchParams.toString()) {
            // URL의 쿼리 파라미터가 변경될 때마다 실행
            fetchFilteredPosts();
          }
    }, [searchParams]); // 이 useEffect는 searchParams의 변화에 따라 실행됩니다.


    // 글 제목 클릭 시 해당 글의 상세 페이지로 이동하는 함수
    const handlePostClick = (postId) => {
        navigate(`/posts/${postId}`); 
      };
    

    // 태그 카테고리 변경사항 
    const handleTagChange = tag => {
        const newSelectedTags = selectedTags.includes(tag) ? selectedTags.filter(t => t !== tag) : [...selectedTags, tag];
        setSelectedTags(newSelectedTags);
        updateURL(newSelectedTags, selectedCategory);
    };

    const handleCategoryChange = category => {
        setSelectedCategory(category === 'ALL' ? '' : category);
        updateURL(selectedTags, category === 'ALL' ? '' : category);
    };

    const updateURL = (tags, category) => {
        const tagsQuery = tags.join('&');
        setSearchParams({ tags: tagsQuery, cat: category });
    };

    const buildQueryString = () => {
        const tagsQuery = selectedTags.join('&');
        return `tags=${tagsQuery}${selectedCategory ? `&cat=${selectedCategory}` : ''}`;
    };

    // 태그 버튼 클릭 시 해당 태그를 가진 글로 필터링하는 로직
    // const filterPostsByTag = (tag) => {
    //     navigate(`/tags/${encodeURIComponent(tag)}`);
    //   };
    
    // 상단 태그 버튼 클릭 시 이벤트
    // const handleTagClick = (tag, event) => {
    //     event.stopPropagation();
    //     filterPostsByTag(tag);
    // };

    // const allTags = posts.reduce((acc, post) => [...acc, ...post.tags], []);
    // const uniqueTags = [...new Set(allTags)];

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;
    
    return (
    <Wrapper>
        <h1>블로그 메인/리스트 페이지</h1>
        <TagContainer>
            {uniqueTags.map((tag, tagIndex) => (
                <Tags key={tagIndex} style={{marginRight: "10px", cursor: 'pointer', gap: "10px" }} onClick={(event) => handleTagClick(tag, event)}>
                    #{tag}
                </Tags>     
            ))}
        </TagContainer>
        <div>
            {posts.map((post, index) => (
                <div key={index}>
                    <h2 onClick={() => handlePostClick(post._id)} style={{ cursor: 'pointer' }}>
                        {post.title}
                    </h2>
                    <h4>{post.outline}</h4>
                    {post.tags.map((tag, tagIndex) => (
                        <Tags key={tagIndex} style={{marginRight: "10px", cursor: 'pointer', gap: "10px" }} onClick={(event) => handleTagClick(tag, event)}>
                            #{tag}
                        </Tags>
                    ))}
                </div>
            ))}
        </div>
    </Wrapper>
    );
};

export default BlogListPage;
