import React from "react";
import { useEffect, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import styled, { createGlobalStyle } from "styled-components";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding: 40px;
`;

const TagContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  //width: 600px;
  gap: 7px;
`;

const CategoryContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  //width: 600px;
  gap: 7px;
`;

const Tags = styled.span`
  padding-top: 3px;
  padding-bottom: 3px;
  padding-right: 8px;
  padding-left: 8px;
  background-color: #ebebeb;
  text-align: center;
  font-size: 13px;
  justify-content: center;
  color: #666666;
  border-radius: 5px;
`;

const BlogListPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchParams, setSearchParams] = useSearchParams();

  // 페이지 이동
  const navigate = useNavigate();

  // 필터링 초기화
  const resetFilters = () => {
    setSelectedTags([]);
    setSelectedCategory("");
    setSearchParams({});
  };

  // 날짜 정렬
  const sortPostsByDate = (posts) => {
    return posts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  // 초기 태그와 카테고리 목록 상태 추가
  const [allTags, setAllTags] = useState([]);
  const [allCategories, setAllCategories] = useState([]);

  // 초기 데이터 로딩
  useEffect(() => {
    // 전체 태그와 카테고리 로딩
    const fetchAllTagsAndCategories = async () => {
      try {
        const tagsResponse = await fetch("http://localhost:8000/tags");
        const categoriesResponse = await fetch(
          "http://localhost:8000/categories"
        );
        if (!tagsResponse.ok || !categoriesResponse.ok) {
          throw new Error("Failed to fetch");
        }
        const tagsData = await tagsResponse.json();
        const categoriesData = await categoriesResponse.json();

        setAllTags(tagsData);
        setAllCategories(categoriesData);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchAllTagsAndCategories();
  }, []); // 빈 의존성 배열로 마운트 시에만 실행

  useEffect(() => {
    // URL 쿼리 파라미터를 사용하여 백엔드 요청 URL 구성
    const tagsFromURL = searchParams.get("tags");
    const categoryFromURL = searchParams.get("cat");

    let queryParams = new URLSearchParams();

    // tagsFromURL이 있으면 각 태그를 queryParams에 추가
    if (tagsFromURL) {
      tagsFromURL.split("&").forEach((tag) => {
        queryParams.append("tags", tag);
      });
    }

    // categoryFromURL이 있으면 queryParams에 추가
    if (categoryFromURL) {
      queryParams.set("cat", categoryFromURL);
    }

    const fetchPosts = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(
          `http://localhost:8000/posts/?${queryParams}`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch posts");
        }
        const data = await response.json();
        const sortedPosts = sortPostsByDate(data); // 받아온 데이터를 날짜 순으로 정렬
        setPosts(sortedPosts);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, [searchParams]); // searchParams의 변화에 따라 실행

  // 글 제목 클릭 시 해당 글의 상세 페이지로 이동하는 함수
  const handlePostClick = (postId) => {
    navigate(`/posts/${postId}`);
  };

  // 태그 단일선택 처리 (공통사용)
  const handleTagClick = (tag, event) => {
    event.stopPropagation();
    navigate(`/?tags=${tag}`);
  };

  // 태그 다중선택 처리
  const handleTagChange = (tag) => {
    const newSelectedTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];
    setSelectedTags(newSelectedTags);
    setSearchParams({ tags: newSelectedTags.join("&"), cat: selectedCategory });
  };

  // 카테고리 선택 처리
  const handleCategoryChange = (category) => {
    const isDeselecting = selectedCategory === category || category === "ALL";
    const newCategory = isDeselecting ? "" : category;
    setSelectedCategory(newCategory);

    if (newCategory === "") {
      setSearchParams(
        selectedTags.length > 0 ? { tags: selectedTags.join("&") } : {}
      );
    } else {
      setSearchParams({ tags: selectedTags.join("&"), cat: newCategory });
    }
  };

  // // posts Data 필터링
  // const filteredPosts = posts.filter(post => {
  //     const hasSelectedTags = selectedTags.length === 0 || selectedTags.every(tag => post.tags.includes(tag));
  //     const hasSelectedCategory = !selectedCategory || post.categories.includes(selectedCategory);
  //     return hasSelectedTags && hasSelectedCategory;
  // });

  // const allTags = posts.reduce((acc, post) => [...acc, ...post.tags], []);
  // const uniqueTags = [...new Set(allTags)];

  const uniqueTags = [...new Set(posts.flatMap((post) => post.tags))];
  const uniqueCategories = [...new Set(posts.map((post) => post.categories))];

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <Wrapper>
      <h1 onClick={resetFilters} style={{ cursor: "pointer" }}>
        블로그 메인/리스트 페이지
      </h1>

      

      {/* 태그 렌더링 - 다중 선택 */}
      <TagContainer>
        {uniqueTags.map((tag, tagIndex) => (
          <Tags
            key={tagIndex}
            onClick={() => handleTagChange(tag)}
            style={{ marginRight: "10px", cursor: "pointer", gap: "10px" }}
          >
            #{tag}
            {selectedTags.includes(tag) ? " (Selected)" : ""}
          </Tags>
        ))}
      </TagContainer>

      {/* 카테고리 버튼 렌더링 */}
      <CategoryContainer>
        {uniqueCategories.map((category, index) => (
          <Tags
            key={index}
            onClick={() => handleCategoryChange(category)}
            style={{ marginRight: "10px", cursor: "pointer", gap: "10px" }}
          >
            {selectedCategory === category
              ? `${category} (Selected)`
              : category}
          </Tags>
        ))}
      </CategoryContainer>

      {/* 포스트 목록 렌더링 및 각종 click 이동 이벤트 */}
      <div>
        {posts.map((post, index) => (
          <div key={index}>
            {/* 제목 클릭 시 포스트로 이동 */}
            <h2
              onClick={() => handlePostClick(post._id)}
              style={{ cursor: "pointer" }}
            >
              {post.title}
            </h2>

            {/* 작성자, 프사, 날짜, 아웃라인 */}
            <div
              style={{
                display: "flex",
                alignItems: "center",
                fontSize: "16px",
                fontWeight: "bold",
                color: "gray",
              }}
            >
              <span>{post.author}</span>
              <img
                src={post.profileImagePath}
                alt="Author's profile"
                style={{ width: "30px", height: "30px", borderRadius: "50%" }}
              />
              <span
                style={{
                  margin: "0 10px",
                  fontWeight: "bold",
                  color: "lightgray",
                }}
              >
                {new Date(post.createdAt).toLocaleDateString()}
              </span>
            </div>

            <h4>{post.outline}</h4>

            {/* 태그 및 단일 필터링 */}
            {post.tags.map((tag, tagIndex) => (
              <Tags
                key={tagIndex}
                onClick={(event) => handleTagClick(tag, event)}
                style={{ marginRight: "10px", cursor: "pointer", gap: "10px" }}
              >
                #{tag}
              </Tags>
            ))}
          </div>
        ))}
      </div>
    </Wrapper>
  );
};

export default BlogListPage;
