const CategoryContainer = styled.div`
  display: flex;
  min-width: 300px; /* ÏµúÏÜå ÎÑàÎπÑ ÏÑ§Ï†ï */
  max-width: 300px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
  flex-direction: column;
