  border-radius: 15px;
  border: none;
  background-color: #ebebeb;
  color: #666666;
  cursor: pointer;
  margin-left: 10px;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
`;

const PostWrapper = styled.div`
  display: flex;
  flex-direction: column;
  min-width: 750px; /* ÏµúÏÜå ÎÑàÎπÑ ÏÑ§Ï†ï */
  max-width: 750px; /* ÏµúÎåÄ ÎÑàÎπÑ ÏÑ§Ï†ï */
  gap: 16px;
  padding-bottom: 20px;
  margin-bottom: 20px;
`;

const PostContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-width: 750px;
  gap: 16px;
  padding-bottom: 40px;
  margin-bottom: 20px;
  ${(props) =>
    !props.isLast &&
    css`
      border-bottom: 1px solid #d4d4d4;
    `}
`;

const InnerTagContainer = styled.div`
  display: flex;
  flex-direction: row;
`;
const PostTitle = styled.span`
  font-family: "Pretendard-ExtraBold";
  font-size: 23px;
  cursor: pointer;
  transition: color 0.1s ease;
  &:hover {
    color: #666666;
  }
`;

const Category = styled.span`
  font-family: "Pretendard-Medium";
  cursor: pointer;
  color: ${(props) => (props.isSelected ? "#3b82f6" : "#252a2f")};
  transition: color 0.1s ease;
  &:hover {
    color: #3b82f6;
  }
`;

const BlogListPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchParams, setSearchParams] = useSearchParams();
  const [searchQuery, setSearchQuery] = useState("");

  // ÌéòÏù¥ÏßÄ Ïù¥Îèô
  const navigate = useNavigate();

  // ÌïÑÌÑ∞ÎßÅ Ï¥àÍ∏∞Ìôî
  const resetFilters = () => {
    setSelectedTags([]);
    setSelectedCategory("");
    setSearchParams({});
  };

  // ÎÇ†Ïßú Ï†ïÎ†¨
  const sortPostsByDate = (posts) => {
    return posts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  // ÏΩîÎìúÎ∏îÎ°ù ÎßàÏª§ Ï†úÍ±∞ Ìï®Ïàò
  const removeCodeBlockMarkers = (text) => {
    // ÏΩîÎìúÎ∏îÎ°ù ÏãúÏûëÍ≥º ÎÅù ÌëúÏãúÎßå Ï†úÍ±∞ (Ïñ∏Ïñ¥ ÌëúÏãú Ìè¨Ìï®)
    return text.replace(/```[\w+\s]*\n/g, '').replace(/\n```/g, '');
  };

  // Í≤ÄÏÉâ Ïã§Ìñâ Ìï®Ïàò
  const executeSearch = (e) => {
    e.preventDefault(); // Ìèº Í∏∞Î≥∏ Ï†úÏ∂ú Ïù¥Î≤§Ìä∏ Î∞©ÏßÄ

    setSearchParams({
      ...Object.fromEntries(searchParams.entries()),
      search: searchQuery,
    });
  };

  // Í≤ÄÏÉâÏñ¥ ÏûÖÎ†• Ï≤òÎ¶¨ Ï†ÄÏû•
  const handleSearchInputChange = (event) => {
    setSearchQuery(event.target.value); // ÏûÖÎ†•Îêú Í≤ÄÏÉâÏñ¥Î•º Î°úÏª¨ ÏÉÅÌÉúÎ°ú Ï†ÄÏû•
  };

  // Ï¥àÍ∏∞ Ìè¨Ïä§Ìä∏ ÌéòÏù¥ÏßÄ Î°úÎî©
  useEffect(() => {
    fetch("http://localhost:8000/posts/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch posts");
        }
        return response.json();
      })
      .then((data) => {
        const sortedPosts = sortPostsByDate(data); // Î∞õÏïÑÏò® Îç∞Ïù¥ÌÑ∞Î•º ÎÇ†Ïßú ÏàúÏúºÎ°ú Ï†ïÎ†¨
        setPosts(sortedPosts);
        // setPosts(data);
      })
      .catch((error) => {
        setError(error.message);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  // URL ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞ Í∞í Î≥ÄÌôîÏóê Îî∞Îùº Î∞îÎ°ú Î†åÎçîÎßÅÌïòÍ∏∞
  useEffect(() => {
    // URLÏóêÏÑú tagsÏôÄ cat ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞ Í∞í ÏùΩÏñ¥Ïò§Í∏∞
    const tagsFromURL = searchParams.get("tags");
    const categoryFromURL = searchParams.get("cat");
    const searchQueryFromURL = searchParams.get("search");

    // ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Í∞Ä ÏóÜÎäî Í≤ΩÏö∞ Îπà Î∞∞Ïó¥ ÎòêÎäî Îπà Î¨∏ÏûêÏó¥Î°ú Ï¥àÍ∏∞Ìôî
    const tagsArray = tagsFromURL ? tagsFromURL.split("&") : [];
    const category = categoryFromURL ? categoryFromURL : "";

    // Ïª¥Ìè¨ÎÑåÌä∏ ÏÉÅÌÉúÎ•º URLÏùò ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Ïóê ÎßûÍ≤å ÏóÖÎç∞Ïù¥Ìä∏
    setSelectedTags(tagsArray);
    setSelectedCategory(category);
    setSearchQuery(searchQueryFromURL || "");
  }, [searchParams]); // searchParams Î≥ÄÌôî Í∞êÏßÄ

  // Í∏Ä Ï†úÎ™© ÌÅ¥Î¶≠ Ïãú Ìï¥Îãπ Í∏ÄÏùò ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const handlePostClick = (postId) => {
    navigate(`/posts/${postId}`);
  };

  // ÌÉúÍ∑∏ Îã®ÏùºÏÑ†ÌÉù Ï≤òÎ¶¨ (Í≥µÌÜµÏÇ¨Ïö©)
  const handleTagClick = (tag, event) => {
    event.stopPropagation();
    navigate(`/?tags=${tag}`);
  };

  // ÌÉúÍ∑∏ Îã§Ï§ëÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleTagChange = (tag) => {
    const newSelectedTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];
    setSelectedTags(newSelectedTags);
    setSearchParams({ tags: newSelectedTags.join("&"), cat: selectedCategory });
  };

  // Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleCategoryChange = (category) => {
    const isDeselecting = selectedCategory === category || category === "ALL";
    const newCategory = isDeselecting ? "" : category;
    setSelectedCategory(newCategory);

    // ÌòÑÏû¨Ïùò Í≤ÄÏÉâ ÏøºÎ¶¨ (searchQuery)Î•º Ïú†ÏßÄÌïòÎäî ÏÉàÎ°úÏö¥ ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞ Í∞ùÏ≤¥Î•º ÏÉùÏÑ±
    const newSearchParams = {
      ...selectedTags.length > 0 ? { tags: selectedTags.join("&") } : {},
      ...searchQuery ? { search: searchQuery } : {}, // Í≤ÄÏÉâ ÏøºÎ¶¨Í∞Ä ÏûàÏúºÎ©¥ Ï∂îÍ∞Ä
    };

    if (newCategory !== "") {
    // ÏÉàÎ°úÏö¥ Ïπ¥ÌÖåÍ≥†Î¶¨Í∞Ä ÏÑ†ÌÉùÎêòÏóàÏúºÎ©¥, Ïù¥Î•º ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Ïóê Ï∂îÍ∞Ä
      newSearchParams.cat = newCategory;
    }

    // setSearchParamsÎ•º ÏÇ¨Ïö©ÌïòÏó¨ URLÏùò ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Î•º ÏóÖÎç∞Ïù¥Ìä∏
    setSearchParams(newSearchParams);
  };


  // posts Data ÌïÑÌÑ∞ÎßÅ
  const filteredPosts = posts.filter((post) => {
    // ÌÉúÍ∑∏ Ïπ¥ÌÖåÍ≥†Î¶¨ ÌïÑÌÑ∞ÎßÅ
    const hasSelectedTags =
      selectedTags.length === 0 ||
      selectedTags.every((tag) => post.tags.includes(tag));
    const hasSelectedCategory =
      !selectedCategory || post.categories.includes(selectedCategory);

    // Í≤ÄÏÉâÏøºÎ¶¨ ÌïÑÌÑ∞ÎßÅ
    const searchQueryLower = searchQuery.toLowerCase();
    const matchesSearchQuery = searchQueryLower
      ? removeCodeBlockMarkers(post.title.toLowerCase()).includes(searchQueryLower) ||
        removeCodeBlockMarkers(post.outline.toLowerCase()).includes(searchQueryLower) ||
        removeCodeBlockMarkers(post.content.toLowerCase()).includes(searchQueryLower)
      : true;

  return hasSelectedTags && hasSelectedCategory && matchesSearchQuery;
  });

  useEffect(() => {
    window.scrollTo(0, 0); // ÌïÑÌÑ∞ÎßÅÎêú Ìè¨Ïä§Ìä∏Í∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ïä§ÌÅ¨Î°§ÏùÑ Îß® ÏúÑÎ°ú
  }, [filteredPosts]);

  // ÌÉúÍ∑∏, Ïπ¥ÌÖåÍ≥†Î¶¨ Ï§ëÎ≥µ Ï†úÍ±∞ Î∞è Ïò§Î¶ÑÏ∞®Ïàú Ï†ïÎ†¨
  const uniqueTags = [...new Set(posts.flatMap((post) => post.tags))].sort();
  const uniqueCategories = ["ALL", ...new Set(posts.map((post) => post.categories))].sort((a, b) => {
    if(a === "ALL") return -1; 
    if(b === "ALL") return 1; 
    return a.localeCompare(b);
  });

  if (loading) return <div>Loading...</div>
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <HeaderComponent />
      <Wrapper>
        <ImgTagBox>
          <BackgroundImg>
            <span>Shape the Future of Data</span>
            <span>with a Community of Enthusiasts</span>
          </BackgroundImg>

          {/* ÌÉúÍ∑∏ Î†åÎçîÎßÅ - Îã§Ï§ë ÏÑ†ÌÉù */}
          <TagContainer>
            {uniqueTags.map((tag, tagIndex) => (
              <Tags
                key={tagIndex}
                onClick={() => handleTagChange(tag)}
                isSelected={selectedTags.includes(tag)}
              >
                {tag}
              </Tags>
            ))}
          </TagContainer>
        </ImgTagBox>

        {/* Ìè¨Ïä§Ìä∏ Î™©Î°ù Î†åÎçîÎßÅ Î∞è Í∞ÅÏ¢Ö click Ïù¥Îèô Ïù¥Î≤§Ìä∏ */}
        <PostCategoryContainer>
          <PostWrapper>
            {filteredPosts.map((post, index) => (
              <PostContainer
                key={index}
                isLast={index === filteredPosts.length - 1}
              >
                {" "}
                {/* ÎßàÏßÄÎßâ ÏöîÏÜåÏù∏ÏßÄ ÌôïÏù∏ */}
                {/* Ï†úÎ™© ÌÅ¥Î¶≠ Ïãú Ìè¨Ïä§Ìä∏Î°ú Ïù¥Îèô */}
                <PostTitle onClick={() => handlePostClick(post._id)}>
                  {post.title}
                </PostTitle>
                {/* ÏûëÏÑ±Ïûê, ÌîÑÏÇ¨, ÎÇ†Ïßú, ÏïÑÏõÉÎùºÏù∏ */}
                <div
                  style={{ display: "flex", alignItems: "center", gap: "10px" }}
                >
                  <img
                    src={post.profileImagePath}
                    alt="Author's profile"
                    style={{
                      width: "30px",
                      height: "30px",
                      borderRadius: "50%",
                    }}
                  />
                  <span
                    style={{
                      fontFamily: "Pretendard-Medium",
                      color: "#252a2f",
                      marginLeft: "20px",
                    }}
                  >
                    {post.author}
                  </span>
                  <span
                    style={{
                      fontFamily: "Pretendard-Medium",
                      color: "#666666",
                      marginLeft: "20px",
                    }}
                  >
                    {new Date(post.createdAt).toLocaleDateString()}
                  </span>
                </div>
                <span
                  style={{ fontFamily: "Pretendard-Medium", color: "#666666" }}
                >
                  {post.outline}
                </span>
                {/* ÌÉúÍ∑∏ Î∞è Îã®Ïùº ÌïÑÌÑ∞ÎßÅ */}
                <InnerTagContainer>
                  {post.tags.map((tag, tagIndex) => (
                    <Tags
                      key={tagIndex}
                      onClick={(event) => handleTagClick(tag, event)}
                      style={{
                        marginRight: "10px",
                        cursor: "pointer",
                        gap: "10px",
                      }}
                    >
                      {tag}
                    </Tags>
                  ))}
                </InnerTagContainer>
              </PostContainer>
            ))}
            {filteredPosts.length === 0 && (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  gap: "10px",
                }}
              >
                <span
                  style={{
                    fontFamily: "Pretendard-SemiBold",
                    color: "#666666",
                    fontSize: "21px",
                  }}
                >
                  Í≤ÄÏÉâ Í≤∞Í≥ºÏóê Ìï¥ÎãπÌïòÎäî ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§ üòÖ
                </span>
                <span
                  style={{ fontFamily: "Pretendard-Medium", color: "#666666" }}
                >
                  Îã§Î•∏ ÌÉúÍ∑∏ÏôÄ Ïπ¥ÌÖåÍ≥†Î¶¨Î°ú Í≤ÄÏÉâÌï¥Î≥¥ÏÑ∏Ïöî
                </span>
              </div>
            )}
          </PostWrapper>

          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ Î≤ÑÌäº Í≤ÄÏÉâ Í∏∞Îä• Î†åÎçîÎßÅ */}
          <CategoryContainer>
            <SearchForm onSubmit={executeSearch}>
              <SearchInput
                type="text"
                value={searchQuery}
                onChange={handleSearchInputChange} // Î≥ÄÍ≤ΩÎêú Ìï∏Îì§Îü¨ ÏÇ¨Ïö©
                placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              />
              <SearchButton type="submit">üîé</SearchButton>
            </SearchForm>
            <span
              style={{
                fontFamily: "Pretendard-ExtraBold",
                fontSize: "23px",
              }}
            >
              Category
            </span>
            {uniqueCategories.map((category, index) => (
              <Category
                key={index}
                onClick={() => handleCategoryChange(category)}
                isSelected={selectedCategory === category}
              >
                {category}
              </Category>
            ))}
          </CategoryContainer>
        </PostCategoryContainer>
      </Wrapper>
      <BottomComponent />
    </div>
  );
};

export default BlogListPage;
