import React from "react";
import { useEffect, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import styled, { css } from "styled-components";

import HeaderComponent from "../ui/HeaderComponent";
import BottomComponent from "../ui/BottomComponent";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const ImgTagBox = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 40px;
  margin-bottom: 30px;
`;

const BackgroundImg = styled.div`
  width: 100%;
  height: 300px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-image: url("${process.env.PUBLIC_URL}/background.JPG");
  background-size: cover;
  background-position: center;
  opacity: 0.5;
  color: white;
  font-family: "Pretendard-ExtraBold";
  font-size: 60px;
  text-shadow: 0px 0px 3px rgba(0, 0, 0, 0.1);
`;

const PostCategoryContainer = styled.div`
  display: flex;
  padding-top: 30px;
  padding-left: 90px;
  padding-right: 90px;
  flex-direction: row;
  align-items: flex-start;
  gap: 50px;
  justify-content: center; /* 추가: 자식 요소들이 중앙에 위치하도록 함 */
`;

const TagContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  width: 600px;
  gap: 15px 7px;
`;

const CategoryContainer = styled.div`
  display: flex;
  min-width: 300px; /* 최소 너비 설정 */
  max-width: 300px; /* 최대 너비 설정 */
  flex-direction: column;
  gap: 9px;
`;

const Tags = styled.span`
  padding-left: 12px;
  padding-right: 12px;
  display: flex;
  height: 30px;
  cursor: pointer;
  background-color: #ebebeb;
  color: ${(props) =>
    props.isSelected ? "#3b82f6" : "#666666"}; /* 조건부 색상 적용 */
  text-align: center;
  font-family: "Pretendard-SemiBold";
  font-size: 13px;
  justify-content: center;
  align-items: center; /* 이 줄을 추가하세요 */
  border-radius: 30px;
  transition: color 0.1s ease;
  &:hover {
    color: #3b82f6;
  }
`;

const SearchForm = styled.form`
  display: flex;
  flex-direction: row;
  margin-bottom: 20px;
`;

const SearchInput = styled.input`
  height: 30px;
  outline: none;
  padding-left: 10px;
  border-radius: 15px;
  border: none;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
  font-family: "Pretendard-Medium";
`;

const SearchButton = styled.button`
  padding: 5px 10px;
  border-radius: 15px;
  border: none;
  background-color: #ebebeb;
  color: #666666;
  cursor: pointer;
  margin-left: 10px;
  box-shadow: inset 0px 0px 3px rgba(0, 0, 0, 0.1);
`;

const PostWrapper = styled.div`
  display: flex;
  flex-direction: column;
  min-width: 750px; /* 최소 너비 설정 */
  max-width: 750px; /* 최대 너비 설정 */
  gap: 16px;
  padding-bottom: 20px;
  margin-bottom: 20px;
`;

const PostContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-width: 750px;
  gap: 16px;
  padding-bottom: 40px;
  margin-bottom: 20px;
  ${(props) =>
    !props.isLast &&
    css`
      border-bottom: 1px solid #d4d4d4;
    `}
`;

const InnerTagContainer = styled.div`
  display: flex;
  flex-direction: row;
`;
const PostTitle = styled.span`
  font-family: "Pretendard-ExtraBold";
  font-size: 23px;
  cursor: pointer;
  transition: color 0.1s ease;
  &:hover {
    color: #666666;
  }
`;

const Category = styled.span`
  font-family: "Pretendard-Medium";
  cursor: pointer;
  color: ${(props) => (props.isSelected ? "#3b82f6" : "#252a2f")};
  transition: color 0.1s ease;
  &:hover {
    color: #3b82f6;
  }
`;

const BlogListPage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [posts, setPosts] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchParams, setSearchParams] = useSearchParams();
  const [searchQuery, setSearchQuery] = useState("");

  // 페이지 이동
  const navigate = useNavigate();

  // 필터링 초기화
  const resetFilters = () => {
    setSelectedTags([]);
    setSelectedCategory("");
    setSearchParams({});
  };

  // 날짜 정렬
  const sortPostsByDate = (posts) => {
    return posts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  };

  // 코드블록 마커 제거 함수
  const removeCodeBlockMarkers = (text) => {
    // 코드블록 시작과 끝 표시만 제거 (언어 표시 포함)
    return text.replace(/```[\w+\s]*\n/g, '').replace(/\n```/g, '');
  };

  // 검색어 입력 처리 저장
  const handleSearchInputChange = (event) => {
    setSearchQuery(event.target.value); // 입력된 검색어를 로컬 상태로 저장
  };

  // 검색 실행 함수
  const executeSearch = (e) => {
    e.preventDefault(); // 폼 기본 제출 이벤트 방지

    setSearchParams({
      ...Object.fromEntries(searchParams.entries()),
      search: searchQuery,
    });
  };

  // 초기 포스트 페이지 로딩
  useEffect(() => {
    fetch("http://localhost:8000/posts/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch posts");
        }
        return response.json();
      })
      .then((data) => {
        const sortedPosts = sortPostsByDate(data); // 받아온 데이터를 날짜 순으로 정렬
        setPosts(sortedPosts);
        // setPosts(data);
      })
      .catch((error) => {
        setError(error.message);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  // URL 쿼리 파라미터 값 변화에 따라 바로 렌더링하기
  useEffect(() => {
    // URL에서 tags와 cat 쿼리 파라미터 값 읽어오기
    const tagsFromURL = searchParams.get("tags");
    const categoryFromURL = searchParams.get("cat");
    const searchQueryFromURL = searchParams.get("search");

    // 쿼리 파라미터가 없는 경우 빈 배열 또는 빈 문자열로 초기화
    const tagsArray = tagsFromURL ? tagsFromURL.split("&") : [];
    const category = categoryFromURL ? categoryFromURL : "";

    // 컴포넌트 상태를 URL의 쿼리 파라미터에 맞게 업데이트
    setSelectedTags(tagsArray);
    setSelectedCategory(category);
    setSearchQuery(searchQueryFromURL || "");
  }, [searchParams]); // searchParams 변화 감지

  // 글 제목 클릭 시 해당 글의 상세 페이지로 이동하는 함수
  const handlePostClick = (postId) => {
    navigate(`/posts/${postId}`);
  };

  // 태그 단일선택 처리 (공통사용)
  const handleTagClick = (tag, event) => {
    event.stopPropagation();
    navigate(`/?tags=${tag}`);
  };

  // 태그 다중선택 처리
  const handleTagChange = (tag) => {
    const newSelectedTags = selectedTags.includes(tag)
      ? selectedTags.filter((t) => t !== tag)
      : [...selectedTags, tag];
    setSelectedTags(newSelectedTags);
    setSearchParams({ tags: newSelectedTags.join("&"), cat: selectedCategory });
  };

  // 카테고리 선택 처리
  const handleCategoryChange = (category) => {
    const isDeselecting = selectedCategory === category || category === "ALL";
    const newCategory = isDeselecting ? "" : category;
    setSelectedCategory(newCategory);

    // 현재의 검색 쿼리 (searchQuery)를 유지하는 새로운 쿼리 파라미터 객체를 생성
    const newSearchParams = {
      ...selectedTags.length > 0 ? { tags: selectedTags.join("&") } : {},
      ...searchQuery ? { search: searchQuery } : {}, // 검색 쿼리가 있으면 추가
    };

    if (newCategory !== "") {
    // 새로운 카테고리가 선택되었으면, 이를 쿼리 파라미터에 추가
      newSearchParams.cat = newCategory;
    }

    // setSearchParams를 사용하여 URL의 쿼리 파라미터를 업데이트
    setSearchParams(newSearchParams);
  };


  // posts Data 필터링
  const filteredPosts = posts.filter((post) => {
    // 태그 카테고리 필터링
    const hasSelectedTags =
      selectedTags.length === 0 ||
      selectedTags.every((tag) => post.tags.includes(tag));
    const hasSelectedCategory =
      !selectedCategory || post.categories.includes(selectedCategory);

      const matchesSearchQuery = searchParams.get('search') ? 
      post.title.toLowerCase().includes(searchParams.get('search').toLowerCase()) || 
      post.content.toLowerCase().includes(searchParams.get('search').toLowerCase()) : true;
  return hasSelectedTags && hasSelectedCategory && matchesSearchQuery;
});

  useEffect(() => {
    window.scrollTo(0, 0); // 필터링된 포스트가 변경될 때마다 스크롤을 맨 위로
  }, [filteredPosts]);

  // 태그, 카테고리 중복 제거 및 오름차순 정렬
  const uniqueTags = [...new Set(posts.flatMap((post) => post.tags))].sort();
  const uniqueCategories = ["ALL", ...new Set(posts.map((post) => post.categories))].sort((a, b) => {
    if(a === "ALL") return -1; 
    if(b === "ALL") return 1; 
    return a.localeCompare(b);
  });

  if (loading) return <div>Loading...</div>
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <HeaderComponent />
      <Wrapper>
        <ImgTagBox>
          <BackgroundImg>
            <span>Shape the Future of Data</span>
            <span>with a Community of Enthusiasts</span>
          </BackgroundImg>

          {/* 태그 렌더링 - 다중 선택 */}
          <TagContainer>
            {uniqueTags.map((tag, tagIndex) => (
              <Tags
                key={tagIndex}
                onClick={() => handleTagChange(tag)}
                isSelected={selectedTags.includes(tag)}
              >
                {tag}
              </Tags>
            ))}
          </TagContainer>
        </ImgTagBox>

        {/* 포스트 목록 렌더링 및 각종 click 이동 이벤트 */}
        <PostCategoryContainer>
          <PostWrapper>
            {filteredPosts.map((post, index) => (
              <PostContainer
                key={index}
                isLast={index === filteredPosts.length - 1}
              >
                {" "}
                {/* 마지막 요소인지 확인 */}
                {/* 제목 클릭 시 포스트로 이동 */}
                <PostTitle onClick={() => handlePostClick(post._id)}>
                  {post.title}
                </PostTitle>
                {/* 작성자, 프사, 날짜, 아웃라인 */}
                <div
                  style={{ display: "flex", alignItems: "center", gap: "10px" }}
                >
                  <img
                    src={post.profileImagePath}
                    alt="Author's profile"
                    style={{
                      width: "30px",
                      height: "30px",
                      borderRadius: "50%",
                    }}
                  />
                  <span
                    style={{
                      fontFamily: "Pretendard-Medium",
                      color: "#252a2f",
                      marginLeft: "20px",
                    }}
                  >
                    {post.author}
                  </span>
                  <span
                    style={{
                      fontFamily: "Pretendard-Medium",
                      color: "#666666",
                      marginLeft: "20px",
                    }}
                  >
                    {new Date(post.createdAt).toLocaleDateString()}
                  </span>
                </div>
                <span
                  style={{ fontFamily: "Pretendard-Medium", color: "#666666" }}
                >
                  {post.outline}
                </span>
                {/* 태그 및 단일 필터링 */}
                <InnerTagContainer>
                  {post.tags.map((tag, tagIndex) => (
                    <Tags
                      key={tagIndex}
                      onClick={(event) => handleTagClick(tag, event)}
                      style={{
                        marginRight: "10px",
                        cursor: "pointer",
                        gap: "10px",
                      }}
                    >
                      {tag}
                    </Tags>
                  ))}
                </InnerTagContainer>
              </PostContainer>
            ))}
            {filteredPosts.length === 0 && (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  gap: "10px",
                }}
              >
                <span
                  style={{
                    fontFamily: "Pretendard-SemiBold",
                    color: "#666666",
                    fontSize: "21px",
                  }}
                >
                  검색 결과에 해당하는 내용이 없습니다 😅
                </span>
                <span
                  style={{ fontFamily: "Pretendard-Medium", color: "#666666" }}
                >
                  다른 태그와 카테고리로 검색해보세요
                </span>
              </div>
            )}
          </PostWrapper>

          {/* 카테고리 버튼 검색 기능 렌더링 */}
          <CategoryContainer>
            <SearchForm onSubmit={executeSearch}>
              <SearchInput
                type="text"
                value={searchQuery}
                onChange={handleSearchInputChange} // 변경된 핸들러 사용
                placeholder="검색어를 입력하세요"
              />
              <SearchButton type="submit">🔎</SearchButton>
            </SearchForm>
            <span
              style={{
                fontFamily: "Pretendard-ExtraBold",
                fontSize: "23px",
              }}
            >
              Category
            </span>
            {uniqueCategories.map((category, index) => (
              <Category
                key={index}
                onClick={() => handleCategoryChange(category)}
                isSelected={selectedCategory === category}
              >
                {category}
              </Category>
            ))}
          </CategoryContainer>
        </PostCategoryContainer>
      </Wrapper>
      <BottomComponent />
    </div>
  );
};

export default BlogListPage;
