import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import styled from "styled-components";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import { Prism as SyntaxHighlighter } from "react-syntax-highlighter";
import { prism } from "react-syntax-highlighter/dist/esm/styles/prism";

import HeaderComponent from "../ui/HeaderComponent";

const BottomComponent = styled.div`
  width: 100%;
  height: 300px;
  background-color: #dddddd;
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding: 40px;
`;

const Tags = styled.span`
  padding-top: 3px;
  padding-bottom: 3px;
  padding-right: 8px;
  padding-left: 8px;
  background-color: #ebebeb;
  text-align: center;
  font-size: 13px;
  justify-content: center;
  color: #666666;
  border-radius: 5px;
`;

const ListButton = styled.button`
  border: 1px solid #cccccc;
  padding: 20px;
  margin-top: 20px;
  cursor: pointer;
  border-radius: 20px;
  color: #666666;
  background-color: white;
  width: 190px;
  height: 40px;
  margin: 0 auto;
  font-weight: bold;
  font-size: 17px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const MoveButton = styled.button`
  position: relative;
  border: 1px solid #cccccc;
  padding: 20px;
  margin-top: 20px;
  cursor: pointer;
  border-radius: 10px;
  color: black;
  background-color: white;
  width: 320px;
  height: 120px;
  font-weight: bold;
  font-size: 25px;
  box-size: border-box;
  display: flex;
  flex-direction: column;
  align-items: ${({ alignLeft }) => (alignLeft ? "flex-start" : "flex-end")};
  justify-content: center;
  margin-left: ${({ alignLeft }) => (alignLeft ? "80px" : "auto")};
  margin-right: ${({ alignLeft }) => (alignLeft ? "auto" : "80px")};
  overflow: hidden;

  @media (max-width: 768px) {
    width: 230px;
    height: 55px;
    padding: 10px;
    margin: 10px auto;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 50px;
  width: 100%;

  > *:not(:last-child) {
    margin-right: 10px; /* Add margin between buttons */
  }
`;

const SubTitle = styled.span`
  font-size: 16px;
  color: #666666;
  text-align: ${({ alignLeft }) => (alignLeft ? "left" : "right")};
  margin-bottom: 10px;
  position: absolute;
  top: 17px;
`;

const PostTitle = styled.span`
  font-size: 20px;
  text-align: left;
  position: absolute;
  top: 50%; /* Center vertically */
  left: 50%; /* Center horizontally */
  transform: translate(-50%, -50%); /* Move to center */
  width: 90%; /* Adjust width */
  text-overflow: ellipsis; /* Add ellipsis for overflow */
  white-space: nowrap; /* Prevent wrapping */
  overflow: hidden; /* Hide overflow */

  @media (max-width: 768px) {
    display: none; /* Hide PostTitle on small screens */
  }
`;

const Body = styled.div`
  font-size: 17px;
  width: 90%;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: left;
`;

const Divider = styled.hr`
  border: none;
  height: 1px;
  background-color: #ced4da;
  margin-top: 40px;
  margin-bottom: 40px;
  width: 90%;
`;

const CodeBlock = ({ node, inline, className, children, ...props }) => {
  const match = /language-(\w+)/.exec(className || "");
  return !inline && match ? (
    <SyntaxHighlighter
      style={prism}
      language={match[1]}
      PreTag="div"
      {...props}
    >
      {String(children).replace(/\n$/, "")}
    </SyntaxHighlighter>
  ) : (
    <code className={className} {...props}>
      {children}
    </code>
  );
};

const Image = ({ node, ...props }) => (
  <img
    {...props}
    style={{
      flex: "column",
      maxWidth: "auto",
      height: "450px",
      justifyItems: "center",
    }}
  />
);

const ContentPage = () => {
  const { postId } = useParams();
  const [post, setPost] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:8000/posts/${postId}`)
      .then((response) => response.json())
      .then((data) => setPost(data))
      .catch((error) => console.log(error));

    fetch(`http://localhost:8000/posts`)
      .then((response) => response.json())
      .then((data) => setPosts(data))
      .catch((error) => console.log(error));
  }, [postId]);

  const handleTagClick = (tag, event) => {
    event.stopPropagation();
    navigate(`/?tags=${tag}`);
  };

  if (!post || !posts.length) return <div>Loading...</div>;

  const currentIndex = posts.findIndex((p) => p._id === postId);
  const prevPost = currentIndex > 0 ? posts[currentIndex - 1] : null;
  const nextPost =
    currentIndex < posts.length - 1 ? posts[currentIndex + 1] : null;

  const goToPost = (postId) => {
    navigate(`/posts/${postId}`);
  };

  return (
    <div>
      <HeaderComponent />
      <Wrapper>
        <div>
          <h2 style={{ textAlign: "left" }}>{post.title}</h2>
          {post.tags.map((tag, tagIndex) => (
            <Tags
              key={tagIndex}
              onClick={(event) => handleTagClick(tag, event)}
            >
              #{tag}
            </Tags>
          ))}
          <Body>
            <ReactMarkdown
              remarkPlugins={[remarkGfm]}
              components={{ code: CodeBlock, img: Image }}
            >
              {post.content}
            </ReactMarkdown>
          </Body>
        </div>
        <Divider />
        <ListButton onClick={() => navigate("/")}>목록으로 돌아가기</ListButton>
        <ButtonContainer>
          {prevPost && !nextPost && (
            <MoveButton alignLeft onClick={() => goToPost(prevPost._id)}>
              <SubTitle alignLeft>이전 글</SubTitle>
              <PostTitle>{prevPost.title}</PostTitle>
            </MoveButton>
          )}
          {!prevPost && nextPost && (
            <MoveButton alignRight onClick={() => goToPost(nextPost._id)}>
              <SubTitle alignRight>다음 글</SubTitle>
              <PostTitle>{nextPost.title}</PostTitle>
            </MoveButton>
          )}
          {prevPost && nextPost && (
            <>
              <MoveButton alignLeft onClick={() => goToPost(prevPost._id)}>
                <SubTitle alignLeft>이전 글</SubTitle>
                <PostTitle>{prevPost.title}</PostTitle>
              </MoveButton>
              <MoveButton alignRight onClick={() => goToPost(nextPost._id)}>
                <SubTitle alignRight>다음 글</SubTitle>
                <PostTitle>{nextPost.title}</PostTitle>
              </MoveButton>
            </>
          )}
        </ButtonContainer>
        <BottomComponent />
      </Wrapper>
    </div>
  );
};

export default ContentPage;
